script:
function success_alert(doc){

    frappe.show_alert({
        message:__(doc.name+' Successfully Created'),
        indicator:'green'
    }, 6);

}

function process_work_orders(listview){

    let is_valid = true;
    let selected_item_list = listview.get_checked_items();

    for(var k = 0; k < selected_item_list.length; k++){
        if(selected_item_list[k].status === 'Draft' && is_valid === true){
            frappe.msgprint("All Work Orders need to be submitted!");
            is_valid = false;
        }
    }

    if(is_valid === true){

        for(var j = 0; j < selected_item_list.length; j++){

                frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Work Order",
                    name: selected_item_list[j].name,
                },
                callback(r) {
                    if(r.message) {

                        var task = r.message;
                        var data = task;

                        frappe.call({
                            method: "frappe.client.get",
                            args: {
                                doctype: "BOM",
                                name: data.bom_no
                            },
                            callback(r) {
                                if(r.message) {

                                    var task1 = r.message;

                                	frappe.xcall('erpnext.manufacturing.doctype.work_order.work_order.make_stock_entry', {
                                					'work_order_id': data.name,
                                					'purpose': 'Manufacture',
                                					'qty': data.qty
                        			}).then(stock_entry => {

                        				let returned_obj = stock_entry;

                                        frappe.db.insert(returned_obj).then(function(doc) { 
                                                frappe.call({
                                                    "method": "frappe.client.submit",
                                                    "args": {
                                                         "doc" : doc
                                                     }
                                                });

                                                 cur_list.$checks && cur_list.$checks.prop("checked", false);
                                                 cur_list.on_row_checked();

                                                 success_alert(doc);

                                                 cur_list.$checks.prop("checked", false);
                                                 setTimeout(()=>{
                                                    console.log('Time Break');
                                                 },250);

                                            });
                        			});
                                }
                            }
                        });
                    }
                }
            });
        }
    }
}

function confirm_process(listview){
    frappe.confirm(
        'Are you sure you want to process the orders?',
        function(){
            process_work_orders(listview);
        },
        function(){
           window.close();
        }
    )
}


function check_checkboxes(listview) {

	$.each(cur_list.$checks, (i, el) => {
		let docname = $(el).attr("data-name");
		cur_list.$result
			.find(`.list-row-checkbox[data-name='Work Order']`)
	});

     setTimeout(()=>{
    	if(cur_list.$checks.length===0){
	     setTimeout(()=>{
            listview.page.remove_inner_button('Proceed');
	     },10);
        }
 	},100);

 	setTimeout(()=>{
	if(cur_list.$checks.length>0){
        listview.page.add_inner_button(__("Proceed"), function () {
            confirm_process(listview);
        })
        .addClass("btn-primary");
	}
 	},50);
}


frappe.listview_settings['Work Order'] = {

//   on_row_checked(){
//         // triggers when the primary action is clicked

//         console.log('yooooo');

//       },

    refresh: function(listview) {
        check_checkboxes(listview);

    },

    onload: function(listview) {

        listview.page.add_action_item("Proceed", function() {
            confirm_process(listview);
        });

    },

};

let is_viewed;

frappe.ui.form.on('Work Order', {
	refresh(frm) {
		if(is_viewed !== frm.doc.name){
		     console.log('ssss',frm.doc.name)
			is_viewed = frm.doc.name;
			frm.reload_doc();

		}
	}
})
