script:
function success_alert(){

    frappe.show_alert({
        message:__('All Stock Entries Successfully Created'),
        indicator:'green'
    }, 6);

}

function process_work_orders(listview){

    let is_valid = true;
    let selected_item_list = listview.get_checked_items(true);

    for(var k = 0; k < selected_item_list.length; k++){
        if(selected_item_list[k].status === 'Draft' && is_valid === true){
            frappe.msgprint("All Work Orders need to be submitted!");
            is_valid = false;
        }
    }

    if(is_valid === true){
        frappe.xcall('erpnext.manufacturing.doctype.work_order.work_order.proceed_work_orders', {

                'selected_items': selected_item_list

        }).then(stock_entries => {
            frappe.call({
            method: "frappe.desk.doctype.bulk_update.bulk_update.submit_cancel_or_update_docs",
            args: {
                doctype: 'Stock Entry', 
                action: 'submit',
                docnames: stock_entries,
            },

            }).then((r) => {

                    listview.refresh();

                    cur_list.$checks && cur_list.$checks.prop("checked", false); 
                    cur_list.on_row_checked();
                    success_alert();

    				let failed = r.message;
    				if (!failed) failed = [];

    				if (failed.length && !r._server_messages) {
    					frappe.throw(
    						__("Cannot {0} {1}", [action, failed.map((f) => f.bold()).join(", ")])
    					);
    				}
    				if (failed.length < docnames.length) {
    					frappe.utils.play_sound(action);
    					if (done) done();
    				}
            });
        });
        cur_list.$checks && cur_list.$checks.prop("checked", false); 
        cur_list.on_row_checked();
    }
}

function confirm_process(listview){
    frappe.confirm(
        'Are you sure you want to process the orders?',
        function(){
            process_work_orders(listview);
        },
        function(){
           window.close();
        }
    )
}


frappe.listview_settings['Work Order'] = {

	add_fields: ["bom_no", "status", "sales_order", "qty",
	"produced_qty", "expected_delivery_date", "planned_start_date", "planned_end_date"],

    filters: [["status", "!=", "Stopped"]],

	get_indicator: function(doc) {
		if(doc.status==="Submitted") {
			return [__("Not Started"), "orange", "status,=,Submitted"];
		} else {
			return [__(doc.status), {
				"Draft": "red",
				"Stopped": "red",
				"Not Started": "red",
				"In Process": "orange",
				"Completed": "green",
				"Cancelled": "gray"
			}[doc.status], "status,=," + doc.status];
		}
	},


    onload: function(listview) {

        listview.page.add_action_item("Proceed", function() {
            confirm_process(listview);
        });

    },

};

let is_viewed;

frappe.ui.form.on('Work Order', {
	refresh(frm) {
		if(is_viewed !== frm.doc.name){
			is_viewed = frm.doc.name;
			frm.reload_doc();

		}
	}
})
